import{_ as i,c as o,a as e,b as a,e as t,d as s,w as r,r as d,o as g}from"./app-SU7QrgK0.js";const h={};function u(p,n){const l=d("RouteLink");return g(),o("div",null,[n[8]||(n[8]=e('<h1 id="四书" tabindex="-1"><a class="header-anchor" href="#四书"><span>四书</span></a></h1><div class="literary-quote"> 四书者，《大学》、《中庸》、《论语》、《孟子》也。朱子取程子之意，以为《大学》者，大人之学也。 </div><p>四书是儒家经典的重要组成部分，由南宋朱熹编定，包含了儒家思想的核心内容。这四部经典著作奠定了中国传统文化的思想基础，对后世产生了深远的影响。</p><h2 id="📚-四书概览" tabindex="-1"><a class="header-anchor" href="#📚-四书概览"><span>📚 四书概览</span></a></h2><h3 id="🎓-大学" tabindex="-1"><a class="header-anchor" href="#🎓-大学"><span>🎓 大学</span></a></h3><ul><li><strong>性质</strong>：《礼记》中的一篇</li><li><strong>核心思想</strong>：修身、齐家、治国、平天下</li><li><strong>主要内容</strong>：阐述了儒家的政治哲学和修养理论</li></ul><h3 id="⚖️-中庸" tabindex="-1"><a class="header-anchor" href="#⚖️-中庸"><span>⚖️ 中庸</span></a></h3><ul><li><strong>性质</strong>：《礼记》中的一篇</li><li><strong>核心思想</strong>：中庸之道，不偏不倚</li><li><strong>主要内容</strong>：论述了儒家的道德修养和处世哲学</li></ul><h3 id="👨‍🏫-论语" tabindex="-1"><a class="header-anchor" href="#👨‍🏫-论语"><span>👨‍🏫 论语</span></a></h3><ul><li><strong>性质</strong>：孔子及其弟子的言行录</li><li><strong>核心思想</strong>：仁、义、礼、智、信</li><li><strong>主要内容</strong>：记录了孔子的教育思想和人生智慧</li></ul><h3 id="🌱-孟子" tabindex="-1"><a class="header-anchor" href="#🌱-孟子"><span>🌱 孟子</span></a></h3><ul><li><strong>性质</strong>：孟子及其弟子的言论集</li><li><strong>核心思想</strong>：性善论、民本思想</li><li><strong>主要内容</strong>：发展了孔子的思想，强调人性本善</li></ul><h2 id="📖-学习指南" tabindex="-1"><a class="header-anchor" href="#📖-学习指南"><span>📖 学习指南</span></a></h2><div class="chapter-divider"><span class="ornament">❦</span></div><h3 id="学习顺序建议" tabindex="-1"><a class="header-anchor" href="#学习顺序建议"><span>学习顺序建议</span></a></h3><ol><li><strong>《大学》</strong> - 入门之作，了解儒家修养体系</li><li><strong>《论语》</strong> - 核心经典，掌握孔子思想精髓</li><li><strong>《孟子》</strong> - 深化理解，学习孟子的发展思想</li><li><strong>《中庸》</strong> - 高深境界，领悟中庸之道</li></ol><h3 id="学习方法" tabindex="-1"><a class="header-anchor" href="#学习方法"><span>学习方法</span></a></h3><ul><li><strong>熟读成诵</strong>：反复诵读，体会文字之美</li><li><strong>深思明辨</strong>：思考其中的道理和智慧</li><li><strong>联系实际</strong>：将古人智慧运用到现代生活</li><li><strong>循序渐进</strong>：从浅入深，逐步提高理解</li></ul><div class="page-decoration"><span class="verse">学而时习之，不亦说乎？</span></div><h2 id="🔗-快速导航" tabindex="-1"><a class="header-anchor" href="#🔗-快速导航"><span>🔗 快速导航</span></a></h2>',20)),a("ul",null,[a("li",null,[t(l,{to:"/%E5%9B%BD%E5%AD%A6/%E5%9B%9B%E4%B9%A6/%E5%A4%A7%E5%AD%A6.html"},{default:r(()=>[...n[0]||(n[0]=[s("📖 大学",-1)])]),_:1}),n[1]||(n[1]=s(" - 修身齐家治国平天下",-1))]),a("li",null,[t(l,{to:"/%E5%9B%BD%E5%AD%A6/%E5%9B%9B%E4%B9%A6/%E4%B8%AD%E5%BA%B8.html"},{default:r(()=>[...n[2]||(n[2]=[s("⚖️ 中庸",-1)])]),_:1}),n[3]||(n[3]=s(" - 中庸之道的智慧",-1))]),a("li",null,[t(l,{to:"/%E5%9B%BD%E5%AD%A6/%E5%9B%9B%E4%B9%A6/%E8%AE%BA%E8%AF%AD.html"},{default:r(()=>[...n[4]||(n[4]=[s("👨‍🏫 论语",-1)])]),_:1}),n[5]||(n[5]=s(" - 孔子的言行录",-1))]),a("li",null,[t(l,{to:"/%E5%9B%BD%E5%AD%A6/%E5%9B%9B%E4%B9%A6/%E5%AD%9F%E5%AD%90.html"},{default:r(()=>[...n[6]||(n[6]=[s("🌱 孟子",-1)])]),_:1}),n[7]||(n[7]=s(" - 性善论与民本思想",-1))])]),n[9]||(n[9]=a("hr",null,null,-1)),n[10]||(n[10]=a("div",{class:"annotation"}," 💡 **学习提示**：四书是中国传统文化的瑰宝，建议结合注释和现代解读来学习，既要理解古人的智慧，也要思考其在现代社会的意义。 ",-1))])}const B=i(h,[["render",u]]),E=JSON.parse('{"path":"/%E5%9B%BD%E5%AD%A6/%E5%9B%9B%E4%B9%A6/","title":"四书","lang":"zh-CN","frontmatter":{},"git":{"updatedTime":1759814383000,"contributors":[{"name":"ylmzfun","username":"ylmzfun","email":"ylmzfun@163.com","commits":1,"url":"https://github.com/ylmzfun"}],"changelog":[{"hash":"49d99a91bf888542fc5030384aa7a8a856b8ee5c","time":1759814383000,"email":"ylmzfun@163.com","author":"ylmzfun","message":"优化展示逻辑"}]},"filePathRelative":"国学/四书/README.md"}');export{B as comp,E as data};
