import{_ as o,c as n,a as s,o as a}from"./app-C1B9cmLF.js";const r={};function l(i,t){return a(),n("div",null,[...t[0]||(t[0]=[s('<h1 id="天净沙·秋" tabindex="-1"><a class="header-anchor" href="#天净沙·秋"><span>天净沙·秋</span></a></h1><h2 id="作者" tabindex="-1"><a class="header-anchor" href="#作者"><span>作者</span></a></h2><p>白朴</p><h2 id="体裁" tabindex="-1"><a class="header-anchor" href="#体裁"><span>体裁</span></a></h2><p>元曲·散曲·小令</p><h2 id="简介" tabindex="-1"><a class="header-anchor" href="#简介"><span>简介</span></a></h2><p>白朴的《天净沙·秋》是元代散曲中的精品，与马致远的《天净沙·秋思》同为咏秋名作，但风格迥异。白朴此曲以明丽的色彩和开阔的意境描绘秋景，展现了不同于马致远悲凉秋思的另一种审美情趣，体现了元代散曲的多样性。</p><h2 id="推荐版本" tabindex="-1"><a class="header-anchor" href="#推荐版本"><span>推荐版本</span></a></h2><ul><li>《全元散曲》（中华书局）</li><li>《元曲选》（中华书局）</li><li>《白朴集》（中华书局）</li></ul><h2 id="原文" tabindex="-1"><a class="header-anchor" href="#原文"><span>原文</span></a></h2><p><strong>天净沙·秋</strong></p><p>孤村落日残霞，轻烟老树寒鸦， 一点飞鸿影下。 青山绿水，白草红叶黄花。</p><h2 id="注释" tabindex="-1"><a class="header-anchor" href="#注释"><span>注释</span></a></h2><ul><li><strong>天净沙</strong>：曲牌名，又名《塞上秋》。</li><li><strong>孤村</strong>：偏僻的村庄。</li><li><strong>残霞</strong>：夕阳西下时的晚霞。</li><li><strong>轻烟</strong>：淡淡的炊烟。</li><li><strong>老树</strong>：古老的树木。</li><li><strong>寒鸦</strong>：秋天的乌鸦，给人萧瑟之感。</li><li><strong>飞鸿</strong>：飞翔的大雁。</li><li><strong>影下</strong>：影子投下。</li><li><strong>白草</strong>：秋天枯萎变白的草。</li><li><strong>红叶</strong>：秋天变红的叶子。</li><li><strong>黄花</strong>：秋天开放的黄色花朵，如菊花。</li></ul><h2 id="赏析" tabindex="-1"><a class="header-anchor" href="#赏析"><span>赏析</span></a></h2><p>这首散曲是白朴咏秋的代表作，与马致远《天净沙·秋思》形成鲜明对比，展现了不同的审美取向和艺术风格。</p><p><strong>艺术特色：</strong></p><ol><li><p><strong>色彩绚丽</strong>：全曲运用了丰富的色彩词汇，如&quot;残霞&quot;的红、&quot;轻烟&quot;的淡、&quot;青山绿水&quot;、&quot;白草红叶黄花&quot;，构成了一幅色彩斑斓的秋景图。</p></li><li><p><strong>动静结合</strong>：既有&quot;孤村落日&quot;的静态美，又有&quot;飞鸿影下&quot;的动态美，动静相宜，富有层次感。</p></li><li><p><strong>远近相映</strong>：从&quot;孤村落日&quot;的远景，到&quot;轻烟老树&quot;的中景，再到&quot;飞鸿影下&quot;的近景，层次分明，构图完整。</p></li><li><p><strong>对比鲜明</strong>：与马致远《天净沙·秋思》的萧瑟悲凉形成对比，白朴此曲虽写秋景，却不见悲秋之意，反而呈现出明丽开阔的意境。</p></li></ol><p><strong>表现手法：</strong></p><ol><li><strong>白描手法</strong>：不加修饰地直接描绘景物，简洁明快。</li><li><strong>色彩对比</strong>：青、绿、白、红、黄等色彩的巧妙搭配，形成强烈的视觉效果。</li><li><strong>意象叠加</strong>：通过多个意象的组合，营造出丰富的画面层次。</li></ol><p><strong>思想内容：</strong></p><p>作品表现了作者对自然美的热爱和对生活的积极态度。虽然写的是秋景，但没有传统文学中常见的悲秋情调，而是以欣赏的眼光描绘秋天的美丽。这体现了作者豁达的人生态度和对自然美的敏锐感受力。</p><p><strong>与马致远《天净沙·秋思》的比较：</strong></p><ol><li><strong>情调不同</strong>：马致远的作品充满羁旅愁思，白朴的作品则明丽开朗。</li><li><strong>色彩运用</strong>：马致远多用暗淡色调，白朴则色彩丰富明亮。</li><li><strong>意境差异</strong>：马致远营造的是&quot;断肠人在天涯&quot;的悲凉意境，白朴则展现了秋天的绚烂多彩。</li></ol><p><strong>文学地位：</strong></p>',25)])])}const g=o(r,[["render",l]]),p=JSON.parse('{"path":"/%E8%AF%97%E8%AF%8D/%E5%85%83%E6%9B%B2/%E5%A4%A9%E5%87%80%E6%B2%99%C2%B7%E7%A7%8B.html","title":"天净沙·秋","lang":"zh-CN","frontmatter":{},"git":{"updatedTime":1759814383000,"contributors":[{"name":"ylmzfun","username":"ylmzfun","email":"ylmzfun@163.com","commits":3,"url":"https://github.com/ylmzfun"}],"changelog":[{"hash":"49d99a91bf888542fc5030384aa7a8a856b8ee5c","time":1759814383000,"email":"ylmzfun@163.com","author":"ylmzfun","message":"优化展示逻辑"},{"hash":"f9a877def11d3ef2ab339b26cfd3b4984e92475f","time":1759540869000,"email":"ylmzfun@163.com","author":"ylmzfun","message":"增加国学"},{"hash":"8991152bd4df6899789cf1d8679198e5396e5df7","time":1759328802000,"email":"ylmzfun@163.com","author":"ylmzfun","message":"诗词"}]},"filePathRelative":"诗词/元曲/天净沙·秋.md"}');export{g as comp,p as data};
